import React, { useState } from 'react';
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "./components/ui/card"
import { Button } from "./components/ui/button"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "./components/ui/select"
import { Label } from "./components/ui/label"
import { Input } from "./components/ui/input"
import { AlertCircle, Globe, Mic } from 'lucide-react'
import { Alert, AlertDescription, AlertTitle } from "./components/ui/alert"
import { Progress } from "./components/ui/progress"

const LinguaSyncApp = () => {
  const [file, setFile] = useState(null);
  const [targetLanguage, setTargetLanguage] = useState('');
  const [processing, setProcessing] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setError('');
  };

  const handleLanguageChange = (event) => {
    setTargetLanguage(event.target.value);
    setError('');
  };

  const testBackendConnection = async () => {
    try {
      const response = await fetch('http://localhost:5000/');
      const text = await response.text();
      alert(text);
    } catch (error) {
      alert('Error connecting to backend: ' + error.message);
    }
  };

  const processAudio = async () => {
    if (!file || !targetLanguage) {
      setError('Please select both a file and a target language.');
      return;
    }

    setProcessing(true);
    setError('');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('target_language', targetLanguage);

    try {
      const response = await fetch('http://localhost:5000/translate', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Server responded with an error');
      }

      const data = await response.json();
      setResult(data);
    } catch (error) {
      setError('Error processing audio: ' + error.message);
    } finally {
      setProcessing(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-purple-700 via-fuchsia-500 to-pink-500">
      <div className="w-full max-w-md bg-white rounded-lg shadow-xl p-6">
        <h1 className="text-3xl font-bold text-fuchsia-600 mb-4">LinguaSync AI</h1>
        <h2 className="text-xl text-pink-600 mb-6">Audio Translation (Simulated)</h2>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">Upload Audio</label>
          <input type="file" accept="audio/*" onChange={handleFileChange} className="w-full p-2 border rounded" />
          {file && <p className="mt-2 text-sm text-gray-600">Selected file: {file.name}</p>}
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">Target Language</label>
          <select onChange={handleLanguageChange} value={targetLanguage} className="w-full p-2 border rounded">
            <option value="">Select language</option>
            <option value="es">Spanish</option>
            <option value="fr">French</option>
            <option value="de">German</option>
            <option value="zh">Chinese</option>
            <option value="ja">Japanese</option>
          </select>
        </div>

        {error && <p className="text-red-500 mb-4">{error}</p>}

        <button onClick={testBackendConnection} className="w-full bg-blue-500 text-white rounded py-2 px-4 hover:bg-blue-600 mb-4">
          Test Backend Connection
        </button>

        <button 
          onClick={processAudio} 
          disabled={processing || !file || !targetLanguage}
          className="w-full bg-gradient-to-r from-fuchsia-600 to-pink-600 text-white rounded py-2 px-4 hover:from-fuchsia-700 hover:to-pink-700 disabled:opacity-50"
        >
          {processing ? 'Processing...' : 'Translate Audio'}
        </button>

        {result && (
          <div className="mt-4 p-4 bg-green-100 rounded">
            <h3 className="font-semibold text-green-800">Translation Complete</h3>
            <p className="text-sm text-gray-700">Original: {result.original_text}</p>
            <p className="text-sm text-gray-700">Translated: {result.translated_text}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default LinguaSyncApp;
